SELECT s.TRealSum, r.TSumCC_wt, * FROM t_Sale s 
join (SELECT Docid, SUM(SumCC_wt) TSumCC_wt FROM [S-MARKETA].ElitV_DP.dbo.t_sale_R where DocDate > '2018-05-01'
--join (SELECT Docid, SUM(SumCC_wt) TSumCC_wt FROM t_sale_R where DocDate >= '2018-05-01'
group by Docid) r on r.Docid = s.DocID
where s.TRealSum <> r.TSumCC_wt 
--and s.chid in (100574056,100574093,100574118,100574440,100574832,100575745,100575780,100575834,100575836,100575896,100575912,100575920,100575967,100576003,100576442,100576446,100576451,100576803,100576809,100576848,100576853,100576857,100576861,100576871,100577273,100577775,100577803,100578169,100579155,100579493,100579520,100579529,100579907,100579914,100579927,100580303,100580384,100580394,100580830,100580861)
and s.DocDate > '2017-05-01'
ORDER BY s.DocID

