DECLARE @PublisherCode intSELECT @PublisherCode = CONVERT(int, CONVERT(binary(8), context_info)) - 1FROM master.dbo.sysprocessesWHERE spid = @@SPIDSELECT @PublisherCodeSELECT @@SPIDSELECT context_info,CONVERT(binary(8), context_info),CONVERT(int, CONVERT(binary(8), context_info)),*FROM master.dbo.sysprocessesWHERE spid = @@SPIDSELECT @@SPID AS 'ID', SYSTEM_USER AS 'Login Name', USER AS 'User Name'